mapper.select:
select u.id,u.username,u.mail,u.icon
from fun_user u
where mail like concat(#{username,jdbcType=VARCHAR},'%') and id != #{id,jdbcType=INTEGER}
or username like concat(#{username,jdbcType=VARCHAR},'%') and id != #{id,jdbcType=INTEGER}

select

from fun_user
where username = #{username,jdbcType=VARCHAR}
and password = #{password,jdbcType=VARCHAR}

select id, username, telephone, icon, geder, birthday, addr, mail, per_signature
from fun_user
where username = #{username,jdbcType=VARCHAR}
